import React, { useState, useEffect } from 'react';

function App() {
  const [walletAddress, setWalletAddress] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Check if wallet is already connected
    checkConnection();
  }, []);

  const checkConnection = async () => {
    if (typeof window.ergoConnector !== 'undefined') {
      const isConnected = await window.ergoConnector.nautilus.connect();
      setIsConnected(isConnected);
      if (isConnected) {
        const address = await window.ergo.get_change_address();
        setWalletAddress(address);
      }
    }
  };

  const connectWallet = async () => {
    if (typeof window.ergoConnector !== 'undefined') {
      try {
        const connected = await window.ergoConnector.nautilus.connect();
        setIsConnected(connected);
        if (connected) {
          const address = await window.ergo.get_change_address();
          setWalletAddress(address);
        }
      } catch (error) {
        console.error("Error connecting to Nautilus wallet:", error);
      }
    } else {
      console.error("Nautilus wallet is not installed");
    }
  };

  const disconnectWallet = async () => {
    if (isConnected) {
      await window.ergoConnector.nautilus.disconnect();
      setIsConnected(false);
      setWalletAddress(null);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to Token Flight</h1>
      </header>
      <main>
        {!isConnected ? (
          <button onClick={connectWallet}>Connect Nautilus Wallet</button>
        ) : (
          <div>
            <p>Connected: {walletAddress}</p>
            <button onClick={disconnectWallet}>Disconnect Wallet</button>
          </div>
        )}
        <p>Token distribution system coming soon!</p>
      </main>
    </div>
  );
}

export default App;
